mapper.update:
insert into rest (RENO, RE_NAME, READDR, REIMAGE, REPHONE, REURL, REINFO, CHECKIN, CHECKOUT, RTOTAL, PTOTAL, PSTATUS, REFACILITY, CLICK) select REST_SEQ.NEXTVAL as reSeq, T.* from(
select #{item.reName}, #{item.reAddr}, #{item.reImage}, #{item.rePhone}, #{item.reUrl}, #{item.reInfo}, #{item.checkIn}, #{item.checkout}, #{item.rTotal}, #{item.pTotal}, #{item.pStatus}, #{item.reFacility}, 0 from SYS.DUAL
)T

			insert into room (RNO, RENO,	RONAME, ROPRICE, PLIMIT, PMAXCOUNT, ROINFO, ROFACILITY, ROSIZE, ROCOUNT, ROIMAGE)
			select ROOM_SEQ.NEXTVAL as roSeq, T.*
			from(
						
			select  #{item.reNo}, #{item.roName}, #{item.roPrice}, #{item.pLimit}, #{item.pMaxCount}, #{item.roInfo}, #{item.roFacility}, #{item.roSize}, #{item.roCount},
			#{item.roImage}
			from SYS.DUAL
			 
			)T

mapper.select:
select * from room join rest using(reNo)
where reNo = #{reNo}
order by 2
